mapper.delete:
		delete from ${tablename} where guid = #{data.GUID,jdbcType=VARCHAR}


                and year = #{data.YEAR,jdbcType=VARCHAR}


                and year is null




                and province = #{ data.PROVINCE,jdbcType=VARCHAR}


                and province is null

mapper.insert:
