mapper.delete:
		delete from ${tablename} where guid = #{data.GUID,jdbcType=VARCHAR}


                and year = #{data.YEAR,jdbcType=VARCHAR}


                and year is null




                and province = #{data.PROVINCE,jdbcType=VARCHAR}


                and province is null

mapper.insert:
insert into ${tablename} (GUID,CODE,NAME,SUPERGUID,LEVELNO,ISLEAF,STATUS,REMARK,PINYIN,YEAR,PROVINCE,ADMDIV,DBVERSION) select a.* from(
select #{item.GUID ,jdbcType=VARCHAR}, #{item.CODE ,jdbcType=VARCHAR}, #{item.NAME ,jdbcType=VARCHAR}, #{item.SUPERGUID ,jdbcType=VARCHAR}, #{item.LEVELNO ,jdbcType=INTEGER}, #{item.ISLEAF ,jdbcType=INTEGER}, #{item.STATUS ,jdbcType=VARCHAR}, #{item.REMARK ,jdbcType=VARCHAR}, #{item.PINYIN ,jdbcType=VARCHAR}, #{item.YEAR ,jdbcType=VARCHAR}, #{item.PROVINCE ,jdbcType=VARCHAR}, #{item.ADMDIV ,jdbcType=VARCHAR}, to_date(#{item.DBVERSION,jdbcType=VARCHAR},'yyyymmddhh24miss') from dual
) a
