mapper.insert:
INSERT INTO FASP_T_CAROLE (AGENCY,REMARK1,REMARK2,REMARK3,YEAR,PROVINCE,ROLETYPE,ROLENATURE,STATUS,DIVISION,BANK, ADMDIV,ROLELEVEL,GUID,CODE,NAME,REMARK,ISSYS,ORDERNUM,PAYTYPEFLAG,SALARYTYPEFLAG,SPEACCTTYPEFLAG, BGTTYPEFLAG,DBVERSION) SELECT a.* FROM(
select #{item.AGENCY ,jdbcType=VARCHAR}, #{item.REMARK1 ,jdbcType=VARCHAR}, #{item.REMARK2 ,jdbcType=VARCHAR}, #{item.REMARK3 ,jdbcType=VARCHAR}, #{item.YEAR ,jdbcType=VARCHAR}, #{item.PROVINCE ,jdbcType=VARCHAR}, #{item.ROLETYPE ,jdbcType=INTEGER}, #{item.ROLENATURE ,jdbcType=INTEGER}, #{item.STATUS ,jdbcType=VARCHAR}, #{item.DIVISION ,jdbcType=VARCHAR}, #{item.BANK ,jdbcType=VARCHAR}, #{item.ADMDIV ,jdbcType=VARCHAR}, #{item.ROLELEVEL ,jdbcType=VARCHAR}, #{item.GUID ,jdbcType=VARCHAR}, #{item.CODE ,jdbcType=VARCHAR}, #{item.NAME ,jdbcType=VARCHAR}, #{item.REMARK ,jdbcType=VARCHAR}, #{item.ISSYS ,jdbcType=INTEGER}, #{item.ORDERNUM ,jdbcType=INTEGER}, #{item.PAYTYPEFLAG ,jdbcType=VARCHAR}, #{item.SALARYTYPEFLAG ,jdbcType=VARCHAR}, #{item.SPEACCTTYPEFLAG ,jdbcType=VARCHAR}, #{item.BGTTYPEFLAG ,jdbcType=VARCHAR}, #{item.DBVERSION, jdbcType=TIMESTAMP,typeHandler=com.micro.service.springquartz.mybatis.typehandler.TimestampTypeHandler} FROM DUAL
) a

mapper.delete:
		delete from FASP_T_CAROLE where guid = #{GUID,jdbcType=VARCHAR}


                and year = #{YEAR,jdbcType=VARCHAR}


                and year is null




                and province = #{PROVINCE,jdbcType=VARCHAR}


                and province is null
